svg.script:
const textBadge = '{' +
    '"project_url":"https://github.com/neow3j/neow3j",' +
    '"project_name":"neow3j",' +
    '"backers":"17"}';
const jsonBadge = JSON.parse(textBadge);
document.getElementById("ff-badge-a1").setAttribute("xlink:href", jsonBadge.project_url);
document.getElementById("ff-badge-a2").setAttribute("xlink:href", jsonBadge.project_url);
document.getElementById("ff-badge-a3").setAttribute("xlink:href", jsonBadge.project_url);
document.getElementById("ff-badge-name").textContent=jsonBadge.project_name+':';
document.getElementById("ff-badge-nr").textContent=jsonBadge.backers+' backers';

let path = document.getElementById('ff-badge');
let data = path.getAttribute('d');
let len = (jsonBadge.project_name+':'+jsonBadge.backers+' backers 👍').length;

document.getElementById('ff-badge').setAttribute('d', data.replace('h124', 'h'+(10 + (len*4))));
document.getElementById('ff-badge-svg').setAttribute("viewBox", "0 0 "+ (16 + (len*4)) + " 10");
