// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

abstract contract PayoutBase is Initializable, OwnableUpgradeable {
    /**
     * @dev Maps each userId to its current already payed out amount. The userId never changes
     */
    mapping(bytes32 => uint256) public payedOut;
    string private currencyCode;

    function payoutInit(string memory _currencyCode) internal onlyInitializing {
        __Ownable_init();
        payoutInitUnchained(_currencyCode);
    }

    function payoutInitUnchained(
        string memory _currencyCode
    ) internal onlyInitializing {
        currencyCode = _currencyCode;
    }

    function sendRecover(
        address payable receiver,
        uint256 amount
    ) external virtual;

    function getContractBalance() public view virtual returns (uint);

    /**
     * @dev Gets the tea for the provided address.
     */
    function getPayedOut(bytes32 userId) external view returns (uint256) {
        return payedOut[userId];
    }

    /**
     * @dev Gets the tea for the provided address.
     */
    function getClaimableAmount(
        bytes32 userId,
        uint256 totalPayOut
    ) external view returns (uint256) {
        return totalPayOut - payedOut[userId];
    }

    /**
     * @dev Prepares everything to withdraw the earned amount. The signature has to be created by the contract owner and the signed message
     * is the hash of the concatenation of the account and tea.
     *
     * @param userId The user id that never changes
     * @param totalPayOut The total amount that the user earned.
     * @param v The recovery byte of the signature.
     * @param r The r value of the signature.
     * @param s The s value of the signature.
     */
    function calculateWithdraw(
        bytes32 userId,
        uint256 totalPayOut,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) internal returns (uint256) {
        require(totalPayOut > payedOut[userId], "No new funds to be withdrawn");

        bytes32 payloadHash = keccak256(
            abi.encode(userId, "#", totalPayOut, currencyCode)
        );
        bytes32 messageHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", payloadHash)
        );

        require(
            ecrecover(messageHash, v, r, s) == owner(),
            "Signature no match"
        );
        uint256 old = payedOut[userId];
        payedOut[userId] = totalPayOut;

        return totalPayOut - old;
    }

    function withdraw(
        address payable dev,
        bytes32 userId,
        uint256 totalPayOut,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external virtual;
}

contracts/PayoutERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {PayoutBase} from "./PayoutBase.sol";
import {IERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

contract PayoutERC20 is PayoutBase {
    IERC20Upgradeable token;

    function initialize(
        IERC20Upgradeable _token,
        string memory _symbol
    ) public initializer {
        token = _token;
        payoutInit(_symbol);
    }

    function getContractBalance() public view override returns (uint) {
        return token.balanceOf(address(this));
    }

    /**
     * @dev Send back from contract in case something is wrong. This should rarely happen
     */
    function sendRecover(
        address payable receiver,
        uint256 amount
    ) external override onlyOwner {
        require(
            token.transfer(receiver, amount),
            "Transfer was not successful!"
        );
    }

    /**
     * @dev Withdraw the earned amount. The signature has to be created by the contract owner and the signed message
     * is the hash of the concatenation of the account and tea.
     *
     * @param dev The address to withdraw to.
     * @param userId The user id that never changes
     * @param totalPayOut The total amount that the user earned.
     * @param v The recovery byte of the signature.
     * @param r The r value of the signature.
     * @param s The s value of the signature.
     */
    function withdraw(
        address payable dev,
        bytes32 userId,
        uint256 totalPayOut,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external override {
        uint256 toBePaid = calculateWithdraw(userId, totalPayOut, v, r, s);

        // transfer reverts transaction if not successful.
        require(token.transfer(dev, toBePaid), "Transfer was not successful!");
    }
}

contracts/PayoutEth.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {PayoutBase} from "./PayoutBase.sol";

contract PayoutEth is PayoutBase {
    function initialize() public initializer {
        payoutInit("ETH");
    }

    receive() external payable {}

    function getContractBalance() public view override returns (uint) {
        return address(this).balance;
    }

    /**
     * @dev Send back from contract in case something is wrong. This should rarely happen
     */
    function sendRecover(
        address payable receiver,
        uint256 amount
    ) external override onlyOwner {
        receiver.transfer(amount);
    }

    /**
     * @dev Withdraw the earned amount. The signature has to be created by the contract owner and the signed message
     * is the hash of the concatenation of the account and tea.
     *
     * @param dev The address to withdraw to.
     * @param userId The user id that never changes
     * @param totalPayOut The total amount that the user earned.
     * @param v The recovery byte of the signature.
     * @param r The r value of the signature.
     * @param s The s value of the signature.
     */
    function withdraw(
        address payable dev,
        bytes32 userId,
        uint256 totalPayOut,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external override {
        uint256 toBePaid = calculateWithdraw(userId, totalPayOut, v, r, s);

        // transfer reverts transaction if not successful.
        dev.transfer(toBePaid);
    }
}

